import { useState, useRef } from "react";
import TodoList from "./TodoList";
import { v4 as uuidv4 } from "uuid"; //ランダムな文字列生成される

function App() {

  const [todos, setTodos] = useState([
    {id:1, name:"Todo1", completed:false},
  ]);
  // useState=>変数を管理するためのものフックス
  // todos=>追加していくタスク（オブジェクト）
  // todosが更新されたら画面が再レンダリングされる（更新される）
  // つまり無駄な再レンダリングを防げる
  // setTodos=>todosの中身を更新したいときに使う関数

  const todoNameRef = useRef();
  // useRef使えば簡単に要素（文字列）を取得できる

  const handleAddTodo = () => {
    //タスクを追加する
    const name = todoNameRef.current.value;
    setTodos((prevTodos) => {
      return [...prevTodos, {id: uuidv4(), name: name, completed: false}]
      // ...prevTodos=>今あるタスクたち
      // {}内=>プラスで追加するタスク
    });
    todoNameRef.current.value = null;

  }

  return (
    <div>
    {/* ↑このdiv削除するとエラーなる */}
      <TodoList todos={todos} /> {/* todosという名前でtodosという変数を渡してあげてね */}
      <input type="text" ref={todoNameRef} />
      <button onClick={handleAddTodo}>タスクを追加</button>
      <button>完了したタスクの削除</button>
      <div>残りのタスク:0</div>
    </div>
  );
}

export default App;
